import styles from './Card.module.scss'
import { useState, useContext } from 'react'
import ContentLoader from "react-content-loader"
import AppContext from "../../context"


const Card = ({ id, price, imageUrl, title, onFavorite, onPlus, favorited = false, added = false, loading = false }) => {



  const { isItemAdded } = useContext(AppContext);
  const [isFavorite, setIsFavorite] = useState(favorited);
  const obj = { id, parentId: id, title, imageUrl, price };

  const onClickPlus = () => {
    onPlus(obj);
  };

  const onClickFavorite = () => {
    onFavorite(obj);
    setIsFavorite(!isFavorite);
  };
  return (

    <div className={styles.card}>

      {
        loading ?
          <ContentLoader
            speed={2}
            width={155}
            height={242}
            viewBox="0 0 155 265"
            backgroundColor="#f3f3f3"
            foregroundColor="#ecebeb">
            <rect x="1" y="0" rx="10" ry="10" width="155" height="155" />
            <rect x="0" y="167" rx="5" ry="5" width="155" height="15" />
            <rect x="0" y="187" rx="5" ry="5" width="100" height="15" />
            <rect x="1" y="234" rx="5" ry="5" width="80" height="25" />
            <rect x="124" y="230" rx="10" ry="10" width="32" height="32" />
          </ContentLoader> : <> <div >
            {onFavorite && <img className={styles.favorite}
              src={isFavorite ? "/images/liked.svg" : "/images/unliked.svg"}
              alt='Unliked'
              onClick={onClickFavorite}
            />}

            {onFavorite && ( <img className={styles.favorite} 
            src={isFavorite ? 'img/liked.svg' : 'img/unliked.svg'} 
            alt="Unliked" 
            onClick={onClickFavorite} />

            )}
          </div>
            <img width="100%" height={130} src={imageUrl} alt="sneakers" />
            <h5>{title}</h5>
            <div className="d-flex justify-between align-center">
              <div className="d-flex flex-column">
                <span>Цена:</span>
                <b>{price} руб.</b>
              </div>
              {onPlus && <img
                className={styles.plus}
                onClick={onClickPlus}
                src={isItemAdded(id) ? "/images/btn-checked.svg" : "/images/btn-plus.svg"}
                alt="plus" />}
            </div></>
      }

    </div>
  )
}

export default Card
