import styles from './Card.module.scss'
import { useState, useEffect } from 'react'
import ContentLoader from "react-content-loader"



const Card = ({ id, price, imageUrl, title, onFavourite, onPlus, favorited = false, added = false, loading = false }) => {

    const [isAdded, setIsAdded] = useState(added)
    const [isFavourite, setFavourite] = useState(favorited)
    const obj = { id, title, imageUrl, price };

    const onClickPlus = () => {
        onPlus(obj)
        setIsAdded(!isAdded)
    }

    const onClickFavourite = () => {
        onFavourite(obj);
        setFavourite(!isFavourite);
    }

    return (

        <div className={styles.card}>

            {
                loading ? <ContentLoader
                    speed={2}
                    width={150}
                    height={200}
                    viewBox="0 0 150 200"
                    backgroundColor="#f3f3f3"
                    foregroundColor="#ecebeb"
                >
                    
                    <rect x="0" y="0" rx="5" ry="5" width="150" height="90" />
                    <rect x="0" y="100" rx="5" ry="5" width="150" height="15" />
                    <rect x="0" y="167" rx="5" ry="5" width="100" height="15" />
                    <rect x="0" y="234" rx="5" ry="5" width="80" height="24" />
                    <rect x="124" y="230" rx="5" ry="5" width="32" height="32" />
                </ContentLoader> : <> <div >
                    <img className={styles.favorite}
                        src={isFavourite ? "/images/heart-liked.svg" : "/images/heart-unliked.svg"}
                        alt='Unliked'
                        onClick={onClickFavourite}
                    />
                </div>
                    <img width="100%" height={135} src={imageUrl} alt="sneakers" />
                    <h5>{title}</h5>
                    <div className="d-flex justify-between align-center">
                        <div className="d-flex flex-column">
                            <span>Цена:</span>
                            <b>{price} руб.</b>
                        </div>
                        <img
                            className={styles.plus}
                            onClick={onClickPlus}
                            src={isAdded ? "/images/btn-checked.svg" : "/images/btn-plus.svg"}
                            alt="plus" />
                    </div></>
            }

        </div>
    )
}

export default Card
