import { useEffect, useState } from "react";
import { Routes, Route } from "react-router-dom";
import axios from "axios"
import Drawer from "./components/Drawer/Drawer";
import Header from "./components/Header/Header";
import Home from "./pages/Home";
import Favoutites from "./pages/Favoutites";
import AppContext from "./context";

function App() {

  const [items, setItems] = useState([])
  const [cartItems, setCartItems] = useState([])
  const [favorites, setFavorites] = useState([])
  const [cartOpened, setCartOpened] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [searchValue, setSearchValue] = useState('')

  useEffect(() => {
    async function fetchData() {
      try {
        const [cartResponse, favoritesResponse, itemsResponse] = await Promise.all([
          axios.get('https://63440055b9ab4243cadca972.mockapi.io/cart'),
          axios.get('https://63440055b9ab4243cadca972.mockapi.io/favorite'),
          axios.get('https://63440055b9ab4243cadca972.mockapi.io/items'),
        ]);

        setIsLoading(false);
        setCartItems(cartResponse.data);
        setFavorites(favoritesResponse.data);
        setItems(itemsResponse.data);
      } catch (error) {
        alert('Ошибка при запросе данных ;(');
        console.error(error);
      }
    }

    fetchData();
  }, []);

  const onAddToCart = async (obj) => {
    try {
      const findItem = cartItems.find((item) => Number(item.parentId) === Number(obj.id));
      if (findItem) {
        setCartItems((prev) => prev.filter((item) => Number(item.parentId) !== Number(obj.id)));
        await axios.delete(`https://63440055b9ab4243cadca972.mockapi.io/cart/${findItem.id}`);
      } else {
        setCartItems((prev) => [...prev, obj]);
        const { data } = await axios.post('https://63440055b9ab4243cadca972.mockapi.io/cart', obj);
        setCartItems((prev) =>
          prev.map((item) => {
            if (item.parentId === data.parentId) {
              return {
                ...item,
                id: data.id,
              };
            }
            return item;
          }),
        );
      }
    } catch (error) {
      alert('Ошибка при добавлении в корзину');
      console.error(error);
    }
  }

  const onRemoveItem = (id) => {
    axios.delete(`https://63440055b9ab4243cadca972.mockapi.io/cart/${id}`)
    setCartItems((prev) => prev.filter(item => item.id !== id))
  }

  const onAddToFavourite = async (obj) => {
    try {
      if (favorites.find((favObj) => Number(favObj.id) === Number(obj.id))) {
        axios.delete(`https://63440055b9ab4243cadca972.mockapi.io/favorite/${obj.id}`);
        setFavorites((prev) => prev.filter((item) => Number(item.id) !== Number(obj.id)));
      } else {
        const { data } = await axios.post(
          'https://63440055b9ab4243cadca972.mockapi.io/favorite',
          obj,
        );
        setFavorites((prev) => [...prev, data]);
      }
    } catch (error) {
      alert('Не удалось добавить в закладки');
    }
  };

  const onChangeSearchInput = (event) => {
    setSearchValue(event.target.value)
  }

  const isItemAdded = (id) => {
    return cartItems.some((obj) => Number(obj.parentId) === Number(id));
  }

  return (
    <AppContext.Provider value={{
      items,
      favorites,
      cartItems,
      isItemAdded,
      onAddToFavourite,
      setCartOpened,
      setCartItems
    }}>

      <div className="container clear">
        {cartOpened &&

          <Drawer items={cartItems}
            onCloseCart={() => setCartOpened(false)}
            onRemove={onRemoveItem}
            opened={cartOpened}
          />}

        <Header onClickCart={() => setCartOpened(true)} />
        <Routes>
          <Route exact path="/"
            element={
              <Home
                items={items}
                cartItems={cartItems}
                searchValue={searchValue}
                setSearchValue={setSearchValue}
                onChangeSearchInput={onChangeSearchInput}
                onAddToCart={onAddToCart}
                onAddToFavourite={onAddToFavourite}
                isLoading={isLoading}
              />}
          />

          <Route exact path="favourites" element={<Favoutites />} />
        </Routes>
      </div>
    </AppContext.Provider>

  );
}

export default App;
